import React from 'react';
import styled from 'styled-components';
export default function BottomSheet(){
    var window_width = window.innerWidth;
    var window_height = window.innerHeight;

    if (window_width > 375) {
        window_width = 375;
    }
    const width_standard = 375;
    const height_standard = 811;

    const card_height = window_height*0.8578;
    const card_width = card_height * 0.4842;

    const Container = styled.div`
        position: fixed;
        z-index: 1;
        top: 0px;
        left: 0px;
        width: 100%;
        height: 100%;
        background-color:rgba(0,0,0,0.5);
        display: flex;
        align-items: flex-end;
        justify-content: center;
        display:flex; //display: none;
    `;

    const BottomSheetContent = styled.div`
            //transform: translateY(100%);
            //transition: transform 0.4s ease-in-out;
        `;
    const BottomSheetHeader = styled.div`
        margin-left : auto;
        margin-right : auto;
        background-color: white;
        width: ${39/width_standard*window_width}px;
        height: ${4/height_standard * window_height}px;
        border-radius: 2px; 
    `;

    const BottomSheetBody = styled.div`
        margin-top: ${8/height_standard*window_height}px;
        background-color: white;
        border-radius: 24px 24px 0px 0px;
        width: ${window_width}px;
        height: max-content;
        justify-content: center;
        align-items: center;
    `;

    const BottomSheetSelection = styled.div`
        margin-top:${50/height_standard*window_height}px;
        padding-left:${32/width_standard*window_width}px;
        height:max-content;
        width: 100px;
        height:100px;
        background-color: blue;
        //width: ${311/width_standard*window_width}px;
        //padding-bottom:${34/height_standard*window_height}px;
    `;
    return (
        <Container>
            <BottomSheetContent>
                <BottomSheetHeader/>
                <BottomSheetBody>
                    <BottomSheetSelection>
                        d
                    </BottomSheetSelection>
                </BottomSheetBody>
            </BottomSheetContent>
        </Container>
    );
}
