import React, { useEffect, useRef, useState } from 'react';
import styled from 'styled-components';

var sliderWidth = 321;

const ShowWolluAmountSliderBackground = styled.div`
    width: 321px;
    height: 4px;
    background-color: black; // TODO change
    border-radius: 10px;
`;

export default function ShowAmountSlider() {

    var ShowWolluAmount1 = styled.div`
        height: 4px;
        width: 32px;
        background-color: red;
        border-radius: 10px;
    `;
    var ShowWolluAmount2 = styled.div`
        height: 4px;
        width: 64px;
        background-color: red;
        border-radius: 10px;
    `;
    var ShowWolluAmount3 = styled.div`
        height: 4px;
        width: 396x;
        background-color: red;
        border-radius: 10px;
    `;

    var ShowWolluAmount = ShowWolluAmount1;

    useEffect(()=> {
        let timer = setInterval(() => {
            const min = 1;
            const max = 3;
            const rand = min + Math.random() * (max - min);
            if (rand == 1) {
                ShowWolluAmount = ShowWolluAmount1;                
            }else {
                ShowWolluAmount = ShowWolluAmount2;
            }
        }, 1000);
        return () => clearInterval(timer);
    })

    return (
        <>
            <ShowWolluAmountSliderBackground>
                <ShowWolluAmount/>
            </ShowWolluAmountSliderBackground>
        </>
    );
};
