import React, {useState, useEffect} from 'react';
import styled from 'styled-components';
import "../Css/WolluItemBottomSheet.css";

export default function ThirdPage(){

    const [workingTimeText, setWorkingTimeText] = useState('');
    const [wolluMinuteText, setWolluMinuteText] = useState('');

    //const [wolluMinuteText, setWolluMinuteText] = useState('');
    const onWolluMinuteChange = (e) => {

        if (isNaN(e.target.value)) {
            e.target.value = "";
            alert("숫자만 입력해주세요");
        }  else {
            if (Number(e.target.value) > 60 * 24 ){
                alert("하루를 초과한 시간은 할 수 없습니다.");
                e.target.value = 60 * 24;
            } else if (workingTimeText * 60 < e.target.value){
                alert("일한 시간보다 많을 수 없습니다.");
                e.target.value = "";
            }
            setWolluMinuteText(e.target.value);
        }

    };

    const showBottomSheet = () => {

        let container = document.querySelector("#bottomSheetContainer");
        let bottomSheet = document.querySelector("#bottomSheetContainer #bottomSheet");
        container.classList.add("active");
        setTimeout(() => {
            bottomSheet.classList.add("active");
        }, 1);
        let bodyTag = document.getElementById("root");
        bodyTag.classList.add("StopScroll");

    };

    var window_width = window.innerWidth;
    var window_height = window.innerHeight;

    if (window_width < 375){
      window_width = 375;
    }
    const Background = styled.div`
      width:${window_width}px;
      height:${window_height}px;
      background-color: var(--main-background-color);
      display:flex;
      justify-content: center;
      //align-items: center;
    `;

    const card_height = window_height*0.8578;
    const card_width = card_height * 0.4842;
    const card_margin_top = (window_height - card_height) / 2;

    const Card = styled.div`
    margin-top: ${card_margin_top}px;
    width:${card_width}px;
    height:${card_height}px;
    background-color: #FFFFFF;
    border-radius: 12px;
    filter : drop-shadow(0px 0px 1px #8294EF);
    `;

    const WolluTextFiled = styled.div`
        margin-top: ${72/700 * card_height}px;
        background-color: white;
        width: ${221/339 * card_width}px;
        height: ${52/700 * card_height}px;
        border-color: rgba(0,0,0,0.5);
        margin-left:auto;
        margin-right:auto;
    `;

    return (
      <Background>
      <Card>
        <WolluTextFiled>

        </WolluTextFiled>

      </Card>
      </Background>  
    );
};
