import '../CssUtil/Color.css';
import '../CssUtil/Font.css';

import React, {useState} from 'react';
import styled from 'styled-components';
import WebDownloadViewImage from "../Resources/Images/WebDownloadViewImage.svg";
import WebDownloadViewTeamLogo from "../Resources/Images/WebDownloadViewTeamLogo.svg";

function AppDownloadView() {
  // initialize
  var window_width = window.innerWidth;
  var window_height = window.innerHeight;

  const AppDownloadViewBackground = styled.div`
    width: ${window_width}px;
    height:${window_height}px;
    background-color: var(--main-background-color);
    display:flex;
    justify-content: center;
    align-items: center;
  `;
  const AppDownloadViewInner = styled.div`
    width: max-content;
    height: max-content;
    background-color: blue;
    margin:auto;
    margin-top:auto;
    margin-bottom: auto;
  `;
  const UpperPart = styled.div`
    display:flex;
  `; 
  const LeftPart = styled.div`
    width:100px;
    height:100px;
    background-color: pink;
  `;
  const RightPart = styled.div`
    width: min-content;
    height:min-content;
    background-color: red;
  `;
  const BottomPart = styled.div`
    width:1000px;
    height:100px;
    border-radius: 24px;
    background-color: #FAFAFF;
    display: flex;
  `;
  const TeamLogo = styled.img`
    padding-left: 24px;
    padding-top: 18px;
    width: 85px;
    height: 33px;
  `;

  const TeamText = styled.div`
    color: #000000;
    font-family: "puradack_mac";
    font-weight: normal;
    font-size: 18px;
    padding-left: 8px;
    padding-top: 25px;
  `;

  const TeamIntroText = styled.div`
    color: #424242;
    font-family: "pretendard_regular";
    font-weight: normal;
    font-size: 18px;
    padding-left: 8px;
    padding-top: 25px;
  `;
  return (
    <>
      <AppDownloadViewBackground>
        <AppDownloadViewInner>
          <UpperPart>
            <LeftPart>
              ddddd
            </LeftPart>
            <RightPart>
              <img src={WebDownloadViewImage}/>
            </RightPart>
          </UpperPart>
          <BottomPart>
            <TeamLogo src={WebDownloadViewTeamLogo}/>
            <TeamText>team.이구하나</TeamText>
            <TeamIntroText>우리 팀은 29살에 모인 팀이라서 이구하나입니다.</TeamIntroText>
          </BottomPart>
        </AppDownloadViewInner>
      </AppDownloadViewBackground>
    </>
  );
}

export default AppDownloadView;
