import '../Css/GetWolluView.css';
import '../CssUtil/CardStyle.css';
import '../CssUtil/TextStyle.css';
import '../CssUtil/Spaces.css';
import '../CssUtil/Utils.css';
import GetWolluView_ShowTextImage from "../Resources/Images/GetWolluView_ShowTextImage.svg";
import GetWolluView_WolluImage from "../Resources/Images/GetWolluView_WolluImage.svg";
import bottomSheetItemUnSelected from "../Resources/Images/bottomSheetItemUnSelected.svg";

import React, {useState, FC} from 'react';
import "../Css/WolluItemBottomSheet.css";

//const [wolluMinuteText, setWolluMinuteText] = useState('');

function GetWolluView(wolluMinuteText, setWolluMinuteText, workingTimeText, wollu_item_selected) {


    //const [wolluMinuteText, setWolluMinuteText] = useState('');
    const onWolluMinuteChange = (e) => {
        if (isNaN(e.target.value)) {
            e.target.value = "";
            alert("숫자만 입력해주세요");
        }  else {
            if (Number(e.target.value) > 60 * 24 ){
                alert("하루를 초과한 시간은 할 수 없습니다.");
                e.target.value = 60 * 24;
            } else if (workingTimeText * 60 < e.target.value){
                alert("일한 시간보다 많을 수 없습니다.");
                e.target.value = "";
            }
            setWolluMinuteText(e.target.value);
        }
    };

    const showBottomSheet = () => {
        let container = document.querySelector("#bottomSheetContainer");
        let bottomSheet = document.querySelector("#bottomSheetContainer #bottomSheet");
        container.classList.add("active");
        setTimeout(() => {
            bottomSheet.classList.add("active");
        }, 1);
    };

    const hideBottomSheet = () => {
        let container = document.querySelector("#bottomSheetContainer");
        let bottomSheet = document.querySelector("#bottomSheetContainer #bottomSheet");
        bottomSheet.classList.remove('active');
        setTimeout(()=> {
            container.classList.remove("active");
        }, 500);
    };

    var selection_list = [0,0,0,0,0,0,0];

    const onButtonSheetItemClicked = (selected) => {
        if (selection_list[selected] == 1) {
            selection_list[selected] = 0;
            // off self event

        } else {
            selection_list = [0,0,0,0,0,0,0];
            selection_list[selected] = 1;
            // on target evnet
        }

        var selection_index = 1;
        for (selection_index = 1; selection_index < selection_list.length; selection_index ++) {
            let selected_item_text = document.getElementById("bottomSheetItemText" + selection_index);
            let selected_item_image = document.getElementById("bottomSheetItemImage" + selection_index);

            if (selection_list[selection_index] == 1) {
                selected_item_text.classList.add("bottomSheetItemClickedText");
                selected_item_image.classList.add("bottomSheetItemClickedImage");
                wollu_item_selected = selection_index;
            } else {
                selected_item_text.classList.remove("bottomSheetItemClickedText");
                selected_item_image.classList.remove("bottomSheetItemClickedImage");
                wollu_item_selected = -1;
            }
        }
    };
  return (
    <div className="GetWolluView" id="Card">
        <div id="cardTopSpaces"/>
        <div id="getWolluSpace1"/>
        <img src={GetWolluView_ShowTextImage} id="GetWolluView_ShowTextImage"/>
        <div id="getWolluSpace2"/>
        <img src={GetWolluView_WolluImage} id = "GetWolluView_WolluImage"/>
        <div id="getWolluSpace3"/>
        <div className="PretendaredLight" id="getWolluViewTextStyles" >
            <div>
                <div className="Inline" id="getWolluViewFrontText">오늘</div>
                <div className="Inline">
                    <input className="UnFocusedInputText" id="getWolluViewInputField1" onClick={showBottomSheet} placeholder="항목 선택"></input>
                </div>
                <div className="Inline" id="getWolluViewBackText">으로</div>
            </div>

            <div id="getWolluSpace4"></div>

            <div>
                <div className="Inline">
                    <input className="UnFocusedInputText" id="getWolluViewInputField2" onChange={onWolluMinuteChange} placeholder="30"></input>
                </div>
                <div className="Inline" id="getWolluViewBackText">분 월루했습니다.</div>
            </div>


        </div>

            <div>
            <div id="bottomSheetContainer">
                <div id="bottomSheet">
                    <div id="bottomSheetHeader"></div>
                    <div id="bottomSheetSpace1"></div>
                    <div id="bottomSheetBody">
                        <div id="bottomSheetSpace2"/>
                        <div id="bottomSheeetTextFields">
                            <div className="bottomSheetUnSelected" onClick={() => onButtonSheetItemClicked(1)}>
                                <div id = "bottomSheetItemText1">업무 없음</div>
                                <img id="bottomSheetItemImage1" src={bottomSheetItemUnSelected}/>
                            </div>
                            <div id="bottomSheetItemSpace"/>

                            <div className="bottomSheetUnSelected" onClick={() => onButtonSheetItemClicked(2)}>
                                <div id = "bottomSheetItemText2">커피/간식먹기</div>
                                <img id="bottomSheetItemImage2" src={bottomSheetItemUnSelected}/>
                            </div>
                            <div id="bottomSheetItemSpace"/>

                            <div className="bottomSheetUnSelected" onClick={() => onButtonSheetItemClicked(3)}>
                                <div id = "bottomSheetItemText3">화장실 가기</div>
                                <img id="bottomSheetItemImage3" src={bottomSheetItemUnSelected}/>
                            </div>
                            <div id="bottomSheetItemSpace"/>

                            <div className="bottomSheetUnSelected" onClick={() => onButtonSheetItemClicked(4)}>
                                <div id = "bottomSheetItemText4">인터넷 서핑하기</div>
                                <img id="bottomSheetItemImage4" src={bottomSheetItemUnSelected}/>
                            </div>
                            <div id="bottomSheetItemSpace"/>

                            <div className="bottomSheetUnSelected" onClick={() => onButtonSheetItemClicked(5)}>
                                <div id = "bottomSheetItemText5">담배 피우기</div>
                                <img id="bottomSheetItemImage5" src={bottomSheetItemUnSelected}/>
                            </div>
                            <div id="bottomSheetItemSpace"/>

                            <div className="bottomSheetUnSelected" onClick={() => onButtonSheetItemClicked(6)}>
                                <div id = "bottomSheetItemText6">딴짓하기</div>
                                <img id="bottomSheetItemImage6" src={bottomSheetItemUnSelected}/>
                            </div>
                            <div id="bottomSheetItemSpace"/>


                            <div className="bottomSheetUnSelected" onClick={() => onButtonSheetItemClicked(7)}>
                                <div id = "bottomSheetItemText7">이직 준비</div>
                                <img id="bottomSheetItemImage7" src={bottomSheetItemUnSelected}/>
                            </div>
                        </div>
                        <div id="bottomSheetSpace3"/>
