import '../Css/MainView.css';
import MainView_WolluMainImage from "../Resources/Images/MainView_WolluMainImage.svg";
import MainView_291LogoImage from "../Resources/Images/MainView_291LogoImage.svg";
import React, {useState} from 'react';

function MainView() {

  var typingBool = false; 
  var typingIdx=0; 

// 타이핑될 텍스트를 가져온다 
var typingTxt = "사장님 돈은 제가 가져갑니다."

typingTxt=typingTxt.split(""); // 한글자씩 자른다. 

if(typingBool==false){ 
  // 타이핑이 진행되지 않았다면 
   typingBool=true;     
   var tyInt = setInterval(typing,100); // 반복동작 
} 

const [mainText,setMainText] = useState('');
var showText = "";
function typing(){ 
  if(typingIdx<typingTxt.length){ 
    // 타이핑될 텍스트 길이만큼 반복 
    showText += typingTxt[typingIdx];
    setMainText(showText);
    // 한글자씩 이어준다. 
    typingIdx++; 
    console.log(showText);
   } else{ 
     //끝나면 반복종료 
    clearInterval(tyInt); 
   } 
}
  return (
    <div className="MainView">
      <div id="mainSpace1"/>
      <div id="detailText"> 사장님 돈은 제가 가져갑니다</div>
      ddd{mainText}
      <div id="mainSpace2"/>
      <img src={MainView_WolluMainImage}/>
      <div id="mainSpace3"/>
      <img src={MainView_291LogoImage}/>
      <div id="mainSpace4"/>
    </div>
  );
}

export default MainView;
