import React, {FC} from 'react';
import "../Css/TestView.css";
import bottomSheetItemUnSelected from "../Resources/Images/bottomSheetItemUnSelected.svg";
import bottomSheetItemSelected from "../Resources/Images/bottomSheetItemSelected.svg";


function TestView() {
    const showBottomSheet = () => {
        let container = document.querySelector("#bottomSheetContainer");
        let bottomSheet = document.querySelector("#bottomSheetContainer #bottomSheet");
        container.classList.add("active");
        setTimeout(() => {
            bottomSheet.classList.add("active");
        }, 1);
    };

    const hideBottomSheet = () => {
        let container = document.querySelector("#bottomSheetContainer");
        let bottomSheet = document.querySelector("#bottomSheetContainer #bottomSheet");
        bottomSheet.classList.remove('active');
        setTimeout(()=> {
            container.classList.remove("active");
        }, 500);
    };

    const onButtonSheetItemClicked = (selected) => {
        const id = "#sel" + selected;
        console.debug(id);
        let selected_item = document.getElementById("sel1");
        console.debug(selected_item);
        //
        //selected_item.classList.add("active");
        //let selected_item = document.querySelector(id);
        //selected_item.classList.add("selection_active");
    };

    const onbottomSheetAdjustButtonClicked = () => {
    };

  return (
    <div>
        <button onClick={showBottomSheet}>Show Bottom Sheet</button>
        <div id="bottomSheetContainer">
            <div id="bottomSheet">
                <div id="bottomSheetHeader"></div>
                <div id="bottomSheetSpace1"></div>
                <div id="bottomSheetBody">
                    <div id="bottomSheetSpace2"/>
                    <div id="bottomSheeetTextFields">
                        <div className="bottomSheetUnSelected" id="sel1" onClick={onButtonSheetItemClicked(1)} >
                            <div>업무 없음</div>
                            <img src={bottomSheetItemUnSelected} id="bottomSheetSelectionButton"/>
                        </div>
                        <div id="bottomSheetItemSpace"/>



                    </div>
                    <div id="bottomSheetSpace3"/>
                    <div id="bottomSheetAdjustButton" onClick={hideBottomSheet}>적용하기</div>
                    <div id="bottomSheetSpace4"/>
                </div>
            </div>
        </div>
    </div>
  );
}

export default TestView;
